function extend(target, options, deep) {
    for (name in options) {
        copy = options[name];
        if (deep && copy instanceof Array) {
            target[name] = $.extend(deep, [], copy);
        } else if (deep && copy instanceof Object) {
            target[name] = $.extend(deep, {}, copy);
        } else {
            target[name] = options[name];
        }
    }
    return target;
}

